} else {
  $env:NODE_PATH="/mnt/c/Users/vercc/Documents/Udemy/4-natours/node_modules/mime/cli.js/node_modules:/mnt/c/Users/vercc/Documents/Udemy/4-natours/node_modules/mime/node_modules:/mnt/c/Users/vercc/Documents/Udemy/4-natours/node_modules:/mnt/c/Users/vercc/Documents/Udemy/node_modules:/mnt/c/Users/vercc/Documents/node_modules:/mnt/c/Users/vercc/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../mime/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../mime/cli.js" $args
  }
  $ret=$LASTEXITCODE
